import Vue from "vue"
import Vuex from "vuex"

// Vuexをこのプロジェクト全体で使用する宣言
Vue.use(Vuex)

// vuex.storeインスタンスを生成したものを外で使用できるようにし、main.jsで呼ぶ
export default new Vuex.Store({
    // stateでは全てのファイルで使用できるグローバル変数を定義できる
    state: {
        count: 2
    }
})
